"use strict";var Nav=angular.module("Nav",[]),Main=angular.module("Main",["ui.router","ngAnimate","mm.foundation","ngSanitize","Nav"]);
Main.config(["$stateProvider","$urlRouterProvider",function($stateProvider,$urlRouterProvider){$stateProvider.state("friends",{url:"/friends",templateUrl:"components/friends/_index.html",controller:"FriendsCtrl as myFriends",resolve:{friendsPromise:function(friendsFactory){return friendsFactory.ensureFriends()}}})}]);
Main.controller("FriendsCtrl",["friendsPromise",function(friendsPromise){this.test="Hello World!",this.friends=friendsPromise.data}]);
function friendFactory($http,$q){var me={friends:[]};return me.ensureFriends=function(){return 0==me.friends.length?me.fetchFriends():$q.when(me.friends)},me.fetchFriends=function(){return $http.get("data/friends.json").success(function(data){data.error||(me.friends=data)})},me}
Nav.controller("NavCtrl",["$location",function($location){this.page=$location.path()}]);
Main.factory("friendFactory",friendFactory);